function setTheme(e){if("light"===e)document.getElementById("theme-switcher").checked=!0;else document.getElementById("theme-switcher").checked=!1}window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",(e=>{setTheme(e.matches?"dark":"light")})),window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches?setTheme("dark"):setTheme("light");